import React, { useState } from "react";
import { Box, TextField, IconButton } from "@mui/material";
import SendIcon from "@mui/icons-material/Send";
import { useSocket } from "../../contexts/ChatRoomsContext";
import { MessageDTO } from "../../contexts/ChatRoomsContext";
import { useAuth } from "../../contexts/UserContext";


interface ChatInputBarProps{
  room: string;
}

const ChatInputBar: React.FC<ChatInputBarProps> = ({room}) => {
  const [message, setMessage] = useState("");
  const {sendMessage} = useSocket();
  const {user} = useAuth();
    const [updateTrigger, setUpdateTrigger] = useState<boolean>(true);

  const  handleSend = async () => {
    if (message.trim() !== "") {

      const data: MessageDTO = {group: room, name: user, email: user, text:  message }
      sendMessage(data);
      setMessage("");



      const payload = {
        message,
        date: new Date().toISOString(), 
      };

      try {
        const response = await fetch(`/groupchats/${room}/message/`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(payload),
        });

        if (!response.ok) {
          throw new Error(`Error: ${response.statusText}`);
        }

        return await response.json(); 
      } catch (error) {
        console.error("Failed to send message:", error);
        return null;
      }




    }





  };


  return (
    <Box
      sx={{
        display: "flex",
        alignItems: "center",
        gap: 1, // Spacing between elements
        padding: 1,
        backgroundColor: "background.paper",
        borderTop: "1px solid",
        borderColor: "divider",
      }}
    >
      <TextField
        fullWidth
        variant="outlined"
        placeholder="Type a message..."
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        onKeyDown={(e) => e.key === "Enter" && handleSend()}
      />
      <IconButton color="primary" onClick={handleSend}>
        <SendIcon />
      </IconButton>
    </Box>
  );
};

export default ChatInputBar;
