// ChatRoom.tsx
import React, {useEffect, useRef, useState} from 'react';
import {Box, TextField, Container, Typography, Grid, Divider} from '@mui/material';
import Message from './Message';
import {API_HOST} from '../../hooks/UseFetch';
import {useAuth} from "../../contexts/UserContext";
import {FetchRequest, useFetch} from "../../hooks/UseFetch";
import {useSocket} from '../../contexts/ChatRoomsContext';
import ChatInputBar from './ChatInputBar';;

interface ChatRoomProps {
    room: string;
}

interface ChatMessage {
    sender: string;
    text: string;
    timestamp: string;
}

type Request = {
    updateTrigger: boolean;
    setUpdateTrigger: (value: boolean) => void;
    setData: (data: ChatMessage[]) => void;
    token: string;
    user: string | null;
    endpoint: string;
    queryParams: string;
};

export function generateRequest({
                                    updateTrigger,
                                    setUpdateTrigger,
                                    setData,
                                    token,
                                    user,
                                    endpoint,
                                    queryParams,
                                }: Request) {
    return {
        triggerRequest: updateTrigger,
        setTriggerRequest: setUpdateTrigger,
        callback: (data: any) => {
            setUpdateTrigger(false);
            setData(data);
        },
        method: "GET",
        headers: {
            "Authorization": `Bearer ${token}`,
            "Content-Type": "application/json",
            "X-Consumer-Custom-Id": String(user),
        },

        endpoint: `${API_HOST}/${endpoint}?${queryParams}`,
        payload: null,
    };
}


const ChatRoom: React.FC<ChatRoomProps> = ({room}) => {

    const [oldMessages, setOldMessages] = useState<ChatMessage[]>([]);
    const {getMessagesByRoom} = useSocket();
    const [updateTrigger, setUpdateTrigger] = useState<boolean>(false);
    const newMessages = getMessagesByRoom(room);

    const messagesEndRef = useRef<HTMLDivElement | null>(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({behavior: "smooth",  block: 'end',
           });
    };


    console.log(newMessages);

    const {token} = useAuth();
    const {user} = useAuth();

    const oldMessagesParams: Request = {
        updateTrigger,
        setUpdateTrigger,
        setData: setOldMessages,
        token,
        user,
        endpoint: "",
        queryParams: ""
    };


    const req: FetchRequest = generateRequest(oldMessagesParams);
    const [error, isLoading] = useFetch(req);

    useEffect(() => {
        scrollToBottom();
    }, [newMessages]);


    return (
        <Container>
            <Box sx={{my: 2, height: "600px"}}>
                <Typography variant="h4" gutterBottom>
                    {room}
                </Typography>
                <Box sx={{maxHeight: '550px', overflowY: 'auto', mb: 2}}>
                    {newMessages.map((msg, index) => (
                        <Message
                            key={index}
                            sender={msg.sender}
                            text={msg.text}
                            timestamp={msg.date}
                        />
                    ))}
                    <Divider></Divider>
                    <div ref = {messagesEndRef}> </div>
                </Box>


            </Box>
            <ChatInputBar room={room} />
        </Container>

    );
};


export default ChatRoom;
