import React from 'react';
import { Box, Typography, Paper, Divider, Stack, styled } from '@mui/material';

interface MessageProps {
  sender: string;
  text: string;
  timestamp: string; 
}

// Format date to "Mar 31, 2025, 10:30 AM"
const formatDate = (isoString: string) => {
  const date = new Date(isoString);
  return new Intl.DateTimeFormat('en-US', {
    year: 'numeric',
    month: 'short',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
    hour12: true,
  }).format(date);
};

// Styled Paper component
const StyledPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  backgroundColor: theme.palette.primary.main, // Primary color from the theme
  color: theme.palette.primary.contrastText, // Ensure readable text
  borderRadius: theme.shape.borderRadius,
  maxWidth: '70%',
  display: 'inline-block',
}));

const Message: React.FC<MessageProps> = ({ sender, text, timestamp }) => {
  return (
    <Box sx={{ display: 'flex', justifyContent: 'flex-start', mb: 2 }}>
      <StyledPaper>
        <Stack direction="row" justifyContent="space-between" sx={{ mb: 0.5 }}>
          <Typography variant="caption" color="inherit">
            <strong>{sender}</strong>
          </Typography>
          <Typography variant="caption" color="inherit">
            {formatDate(timestamp)}
          </Typography>
        </Stack>
        <Typography variant="body1" sx={{ wordWrap: 'break-word' }}>
          {text}
        </Typography>
      </StyledPaper>
      <Divider sx={{ mt: 1 }} />
    </Box>
  );
};

export default Message;
