import React, {useState} from "react";
import {Box, TextField, IconButton} from "@mui/material";
import SendIcon from "@mui/icons-material/Send";
import {useSocket} from "../../contexts/ChatRoomsContext";
import {MessageDTO} from "../../contexts/ChatRoomsContext";
import {useAuth} from "../../contexts/UserContext";
import {API_HOST} from "../../hooks/UseFetch";


interface ChatInputBarProps {
    room: string,
    scrollToBottom: () => void
}

async function fetchAPI(room: string, token: string, user: string | null, payload: {
    text: string;
    date: string
}, logOut: () => void) {
    try {
        const response = await fetch(`${API_HOST}/groupchats/${room}/message/`, {
            method: "POST",
            headers: {
                "Authorization": `Bearer ${token}`,
                "Content-Type": "application/json",
                "X-Consumer-Custom-Id": String(user),
            },
            body: JSON.stringify(payload),
        });

        if (response.status === 401) {
            console.warn("Unauthorized request. Redirecting to login...");

            if (user !== null) {
                logOut();
            }

            throw new Error("Unauthorized request. Redirecting to login...");
        }

        if (!response.ok) {
            throw new Error(`Error: ${response.statusText}`);
        }

        return await response.json();
    } catch (error) {
        console.error("Failed to send message:", error);
        return null;
    }
}

const ChatInputBar: React.FC<ChatInputBarProps> = ({room, scrollToBottom}) => {
    const [message, setMessage] = useState("");
    const {sendMessage} = useSocket();
    const {user, token, logOut} = useAuth();

    const handleSend = async () => {
        if (message.trim() !== "") {

            const data: MessageDTO = {group: room, name: user, email: user, text: message}
            sendMessage(data);
            scrollToBottom();
            setMessage("");


            const payload = {
                text: message,
                date: new Date().toISOString(),
            };


            return await fetchAPI(room, token, user, payload, logOut);


        }


    };


    return (
        <Box
            sx={{
                display: "flex",
                alignItems: "center",
                gap: 1, // Spacing between elements
                padding: 1,
                backgroundColor: "background.paper",
                borderTop: "1px solid",
                borderColor: "divider",
            }}
        >
            <TextField
                fullWidth
                variant="outlined"
                placeholder="Type a message..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && handleSend()}
            />
            <IconButton color="primary" onClick={handleSend}>
                <SendIcon/>
            </IconButton>
        </Box>
    );
};

export default ChatInputBar;
