import * as React from 'react';
import {ListItem, ListItemAvatar, Avatar, ListItemText, Typography, Button, Stack} from '@mui/material';
import {useSocket} from '../../contexts/ChatRoomsContext';

interface ChatItemListProps {
    room: string;
    joined: boolean;
}

<Badge badgeContent={4} color="secondary">
    <MailIcon color="action" />
</Badge>

const ChatListItem: React.FC<ChatItemListProps> = ({room, joined,}) => {
    const description = `This is the ${room} group chat`;

    const {joinRoom, leaveRoom, lastMessage} = useSocket();


    const handleJoin = () => {
        joinRoom(room);
    }

    const handleLeave = () => {
        leaveRoom(room);
    }

    return (
        <ListItem alignItems="flex-start">
            <ListItemAvatar>
                <Avatar alt={room} src="random"/>
            </ListItemAvatar>
            <ListItemText
                primary={
                    <Typography
                        component="div"
                        variant="h5"
                        color="primary"
                        sx={{display: 'inline'}}
                    >
                        {room}
                    </Typography>
                }
                secondary={
                    <React.Fragment>
                        <Typography
                            component="span"
                            variant="subtitle1"
                            color="secondary"
                            sx={{display: 'inline'}}
                        >
                            {description}
                        </Typography>

                        <div>

                             {lastMessage(room)}
                        </div>


                    </React.Fragment>
                }
            />
            <Stack direction="row" spacing={2} alignItems="center">
                {!joined && <Button variant="contained" color="primary" onClick={handleJoin}>Join</Button>}
                {joined && <Button variant="contained" color="secondary" onClick={handleLeave}>Leave</Button>}
            </Stack>
        </ListItem>
    );
};

export default ChatListItem;
