import { Box, Paper, Typography, Divider, Stack } from '@mui/material';

const MessageBubble = ({ sender, timestamp, text }) => {
  return (
    <Box sx={{ display: 'flex', justifyContent: 'flex-start', mb: 1 }}>
      <Paper
        sx={{
          p: 1.5,
          backgroundColor: '#f1f1f1',
          borderRadius: 2,
          maxWidth: '70%', // Limits width based on content
          display: 'inline-block', // Matches content size
        }}
      >
        <Stack direction="row" justifyContent="space-between" sx={{ mb: 0.5 }}>
          <Typography variant="caption" color="text.secondary">
            <strong>{sender}</strong>
          </Typography>
          <Typography variant="caption" color="text.secondary">
            {timestamp}
          </Typography>
        </Stack>
        <Typography variant="body1" sx={{ wordWrap: 'break-word' }}>{text}</Typography>
      </Paper>
    </Box>
  );
};

export default MessageBubble;
