import React, { createContext, useContext, useEffect, useState, useCallback } from "react";
import { io, Socket } from "socket.io-client";
import { useAuth } from "./UserContext";
import { API_HOST } from "../hooks/UseFetch";
import { FetchRequest } from "../hooks/UseFetch";
import { useFetch } from "../hooks/UseFetch";

// Define Message type
interface Message {
  sender: string;
  date: string;
  read: boolean;
  received: boolean;
  text: string;
}

// Define Messages Dictionary Type
interface MessagesDictionary {
  [roomId: string]: Message[]; 
}

// Define the Socket Context type
interface SocketContextType {
  socket: Socket | null;
  sendMessage: (event: string, data: any) => void;
  getMessagesByRoom: (roomId: string) => Message[];
  joinedRooms: string[];
  availableRooms: string[];
}

// Create the context
const SocketContext = createContext<SocketContextType | undefined>(undefined);

// Socket.IO server URL
const SOCKET_SERVER_URL = "http://localhost:3000"; // Change this to your backend URL


type Request = {
    updateTrigger: boolean;
    setUpdateTrigger: (value: boolean) => void;
    setData: (data: string[]) => void; 
    token: string;
    user: string | null;
    endpoint: string;
    queryParams: string;
  };



  export const socket = io(SOCKET_SERVER_URL, {
    autoConnect: false
  });

// Provider Component
export const SocketProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [joinedRooms, setJoinedRooms] = useState<string[]>([]);
  const [availableRooms, setAvailableRooms] = useState<string[]>([]);
  const [messages, setMessages] = useState<MessagesDictionary>({});

  const [isConnected, setIsConnected] = useState<boolean>(socket.connected);
    const [updateTrigger, setUpdateTrigger] = useState<boolean>(false);

  const {user} = useAuth();
  const {token} = useAuth();


  const paramsJoined: Request = {updateTrigger, setUpdateTrigger, setData: setJoinedRooms, token, user, endpoint: "", queryParams: "" };
  const paramsAvailable: Request = {updateTrigger, setUpdateTrigger, setData: setAvailableRooms, token, user, endpoint: "", queryParams: "" };

    const [error, isLoading] = useFetch(generateRequest(paramsJoined));
    const [errorA, isLoadingA] = useFetch(generateRequest(paramsAvailable));

    function generateRequest({
      updateTrigger,
      setUpdateTrigger,
      setData,
      token,
      user,
      endpoint,
      queryParams,
    }: Request) {
      return {
        triggerRequest: updateTrigger,
        callback: (data: any) => {
          setUpdateTrigger(false);
          setData(data);

          for (const r in Object.keys(data)) {
            socket.emit("join_chat", r);
          }

        },
        method: "GET",
        headers: {
          "Authorization": `Bearer ${token}`,
          "Content-Type": "application/json",
          "X-Consumer-Custom-Id": String(user),
        },

        endpoint: `${API_HOST}/${endpoint}?${queryParams}`,
        payload: null,
      };
    }

  useEffect(() => {

    function onConnect() {
      socket.emit("join", user);
      setIsConnected(true);
      console.log("Connected to socket:", socket.id);
    }

    socket.on("connect", () => {
      onConnect();
    });

    function onDisconnect() {
      setIsConnected(false);
      console.log("Disconnected from socket");
    }

    socket.on("disconnect", () => {
      onDisconnect();


    });

    // Listen for incoming messages and update the state
    const onMessage = (data: {room: string, sender: string; message: string }) => {
      const newMessage: Message = {
        sender: data.sender,
        date: new Date().toISOString(),
        read: false,
        received: true,
        text: data.message,
      };

      setMessages((prevMessages) => ({
        ...prevMessages,
        [data.room]: [...(prevMessages[data.room] || []), newMessage],
      }));
    };

    socket.on("message", onMessage);


    // Cleanup function to remove event listener and disconnect
    return () => {
      socket.off("connect", onConnect);
      socket.off("disconnect", onDisconnect);
      socket.off("message", onMessage);
      socket.disconnect();
    };
  }, []);

  // Emit function to send messages
  const sendMessage = useCallback((event: string, data: Message) => {
    if (socket) {
      socket.emit(event, data);
    }
  }, [socket]);

  // Function to retrieve messages by room
  const getMessagesByRoom = (roomID: string): Message[] => {

    return messages[roomID] || [];
  };

  return (
    <SocketContext.Provider value={{ socket, sendMessage, getMessagesByRoom, joinedRooms, availableRooms }}>
      {children}
    </SocketContext.Provider>
  );
};

// Custom hook to use the Socket Context
export const useSocket = () => {
  const context = useContext(SocketContext);
  if (!context) {
    throw new Error("useSocket must be used within a SocketProvider");
  }
  return context;
};
