import React, { useState } from "react";
import { Box, TextField, IconButton } from "@mui/material";
import SendIcon from "@mui/icons-material/Send";
import { useSocket } from "../../contexts/ChatRoomsContext";
import { MessageDTO } from "../../contexts/ChatRoomsContext";
import { useAuth } from "../../contexts/UserContext";


interface ChatInputBarProps{
  room: string;
}

const ChatInputBar: React.FC<ChatInputBarProps> = ({room}) => {
  const [message, setMessage] = useState("");
  const {sendMessage} = useSocket();
  const {user} = useAuth();

  const handleSend = () => {
    if (message.trim() !== "") {

      const data: MessageDTO = {group: room, name: user, email: user, text:  message }
      sendMessage(data);
      setMessage("");
    }
  };


function generateRequest({
    updateTrigger,
    setUpdateTrigger,
    setData,
    token,
    user,
    endpoint,
    queryParams,
  }: Request) {
    return {
      triggerRequest: updateTrigger,
      setTriggerRequest: setUpdateTrigger,
      callback: (data: any) => {
        setUpdateTrigger(false);
        setData(data['joined_groupchats']);

        for (const g of data['joined_groupchats']) {
          socket.emit("join_group", {"group": g} );
        }

      },
      method: "GET",
      headers: {
        "Authorization": `Bearer ${token}`,
        "Content-Type": "application/json",
        "X-Consumer-Custom-Id": String(user),
      },

      endpoint: `${API_HOST}/${endpoint}?${queryParams}`,
      payload: null,
    };
  }




  return (
    <Box
      sx={{
        display: "flex",
        alignItems: "center",
        gap: 1, // Spacing between elements
        padding: 1,
        backgroundColor: "background.paper",
        borderTop: "1px solid",
        borderColor: "divider",
      }}
    >
      <TextField
        fullWidth
        variant="outlined"
        placeholder="Type a message..."
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        onKeyDown={(e) => e.key === "Enter" && handleSend()}
      />
      <IconButton color="primary" onClick={handleSend}>
        <SendIcon />
      </IconButton>
    </Box>
  );
};

export default ChatInputBar;
